#install.packages('curl')
library(jsonlite)
seq<-""

search <- function(seq, threshold=1){
  url=paste("http://34.251.101.117:8081/search?threshold=",str(threshold),"&seq=", seq, sep="")
  result <- fromJSON(url)
  return (names(result[[seq]][["results"]]))
}
search(seq)

